import { Layout } from "@/components/layout/Layout";
import { Card } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useState, useEffect, useRef } from "react";
import { TimerDisplay } from "@/components/timer/TimerDisplay";
import { TimerControls } from "@/components/timer/TimerControls";
import { TimerSettings } from "@/components/timer/TimerSettings";

type TimerMode = "countdown" | "stopwatch" | "intervals" | "rounds";

const Timer = () => {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [mode, setMode] = useState<TimerMode>("countdown");
  const [workTime, setWorkTime] = useState(30);
  const [restTime, setRestTime] = useState(10);
  const [currentRound, setCurrentRound] = useState(1);
  const [totalRounds, setTotalRounds] = useState(3);
  const [isResting, setIsResting] = useState(false);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const startSoundRef = useRef<HTMLAudioElement | null>(null);
  const pauseSoundRef = useRef<HTMLAudioElement | null>(null);
  const stopSoundRef = useRef<HTMLAudioElement | null>(null);

  useEffect(() => {
    // Son de d√©marrage (plus aigu)
    startSoundRef.current = new Audio("data:audio/wav;base64,");

    // Son de pause (plus grave)
    pauseSoundRef.current = new Audio("data:audio/wav;base64,");

    // Son d'arr√™t (plus court)
    stopSoundRef.current = new Audio("data:audio/wav;base64,");
  }, []);

  useEffect(() => {
    if (isRunning && !isPaused) {
      intervalRef.current = setInterval(() => {
        setTime((prevTime) => {
          if (mode === "countdown" && prevTime <= 1) {
            playSound(stopSoundRef);
            setIsRunning(false);
            return 0;
          }

          if (mode === "intervals") {
            if (prevTime <= 1) {
              playSound(stopSoundRef);
              setIsResting((prev) => !prev);
              return isResting ? workTime : restTime;
            }
          }

          if (mode === "rounds") {
            if (prevTime <= 1) {
              playSound(stopSoundRef);
              if (isResting) {
                if (currentRound < totalRounds) {
                  setCurrentRound((prev) => prev + 1);
                  setIsResting(false);
                  return workTime;
                } else {
                  setIsRunning(false);
                  setCurrentRound(1);
                  setIsResting(false);
                  return 0;
                }
              } else {
                setIsResting(true);
                return restTime;
              }
            }
          }

          return mode === "stopwatch" ? prevTime + 1 : prevTime - 1;
        });
      }, 1000);
    }
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isRunning, isPaused, mode, workTime, restTime, isResting, currentRound, totalRounds]);

  const toggleTimer = () => {
    if (!isRunning) {
      if (time === 0) {
        if (mode === "countdown") setTime(workTime);
        if (mode === "intervals" || mode === "rounds") setTime(workTime);
      }
      setIsRunning(true);
      setIsPaused(false);
      playSound(startSoundRef);
    } else {
      if (isPaused) {
        setIsPaused(false);
        playSound(startSoundRef);
      } else {
        setIsPaused(true);
        playSound(pauseSoundRef);
      }
    }
  };

  const resetTimer = () => {
    setTime(0);
    setIsRunning(false);
    setIsPaused(false);
    setCurrentRound(1);
    setIsResting(false);
    playSound(stopSoundRef);
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
  };

  const playSound = (soundRef: React.MutableRefObject<HTMLAudioElement | null>) => {
    if (soundRef.current) {
      soundRef.current.currentTime = 0;
      soundRef.current.play().catch((error) => {
        console.error("Error playing sound:", error);
      });
    }
  };

  const handleModeChange = (value: string) => {
    setMode(value as TimerMode);
    resetTimer();
  };

  return (
    <Layout>
      <div className="max-w-2xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-gray-900 mb-8">Chronom√®tre d'Entra√Ænement</h1>

        <Tabs defaultValue="countdown" className="mb-8" onValueChange={handleModeChange}>
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="countdown">Compte √† rebours</TabsTrigger>
            <TabsTrigger value="stopwatch">Chronom√®tre</TabsTrigger>
            <TabsTrigger value="intervals">Intervalles</TabsTrigger>
            <TabsTrigger value="rounds">Rounds</TabsTrigger>
          </TabsList>

          <TabsContent value="countdown" className="mt-4">
            <Card className="p-6">
              <TimerSettings
                mode={mode}
                workTime={workTime}
                restTime={restTime}
                totalRounds={totalRounds}
                onWorkTimeChange={setWorkTime}
                onRestTimeChange={setRestTime}
                onTotalRoundsChange={setTotalRounds}
                isRunning={isRunning}
              />
            </Card>
          </TabsContent>

          <TabsContent value="intervals" className="mt-4">
            <Card className="p-6">
              <TimerSettings
                mode={mode}
                workTime={workTime}
                restTime={restTime}
                totalRounds={totalRounds}
                onWorkTimeChange={setWorkTime}
                onRestTimeChange={setRestTime}
                onTotalRoundsChange={setTotalRounds}
                isRunning={isRunning}
              />
            </Card>
          </TabsContent>

          <TabsContent value="rounds" className="mt-4">
            <Card className="p-6">
              <TimerSettings
                mode={mode}
                workTime={workTime}
                restTime={restTime}
                totalRounds={totalRounds}
                onWorkTimeChange={setWorkTime}
                onRestTimeChange={setRestTime}
                onTotalRoundsChange={setTotalRounds}
                isRunning={isRunning}
              />
            </Card>
          </TabsContent>
        </Tabs>

        <Card className="p-8">
          <TimerDisplay
            time={time}
            currentRound={currentRound}
            totalRounds={totalRounds}
            isResting={isResting}
            mode={mode}
          />
          
          <TimerControls
            isRunning={isRunning}
            isPaused={isPaused}
            onToggle={toggleTimer}
            onReset={resetTimer}
          />
        </Card>
      </div>
    </Layout>
  );
};

export default Timer;